import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import EventSubmission from './components/EventSubmission';
import EventStatus from './components/EventStatus';
import RoomBookingStatus from './components/RoomBookingStatus';
import './App.css';

function App() {
  return (
    <div className="app">
      <nav className="app-nav">
        <div className="nav-content">
          <h1 className="nav-title">User Portal</h1>
          <ul className="nav-links">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/submit-event">Ajukan Event</Link></li>
            <li><Link to="/event-status">Status Event</Link></li>
            <li><Link to="/room-status">Status Ruangan</Link></li>
          </ul>
        </div>
      </nav>

      <main className="app-main">
        <Routes>
          <Route path="/" element={
            <div className="home-container">
              <h2>Selamat Datang di User Portal</h2>
              <p>Silakan pilih menu di atas untuk mengakses fitur yang tersedia.</p>
            </div>
          } />
          <Route path="/submit-event" element={<EventSubmission />} />
          <Route path="/event-status" element={<EventStatus />} />
          <Route path="/room-status" element={<RoomBookingStatus />} />
        </Routes>
      </main>
    </div>
  );
}

export default App;
