import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import EventSubmission from './components/EventSubmission';
import EventStatus from './components/EventStatus';
import RoomBookingStatus from './components/RoomBookingStatus';
import './App.css';

function App() {
  return (
    <div className="app">
      <Navbar />
      <main className="app-main">
        <Routes>
          <Route path="/" element={
            <div className="home-container">
              <h2>Selamat Datang di User Portal</h2>
              <p>Silakan pilih menu di atas untuk mengakses fitur yang tersedia.</p>
            </div>
          } />
          <Route path="/submit-event" element={<EventSubmission />} />
          <Route path="/event-status" element={<EventStatus />} />
          <Route path="/room-status" element={<RoomBookingStatus />} />
        </Routes>
      </main>
    </div>
  );
}

export default App;
